function initContactForm(){const e=document.getElementById("contactForm");if(!e)return;const t=document.getElementById("formSuccess"),n=document.getElementById("formError"),o=document.getElementById("phone");e.addEventListener("submit",i=>{if(i.preventDefault(),t.style.display="none",n.style.display="none",o){const e=function(e){if(!e)return null;let t=String(e).replace(/\D/g,"");if(11===t.length&&t.startsWith("1")&&(t=t.slice(1)),10!==t.length)return null;const n=t.slice(0,3),o=t.slice(3,6);return/[01]/.test(n[0])||/[01]/.test(o[0])?null:{digits:t,pretty:`(${n}) ${o}-${t.slice(6)}`}}(o.value);if(!e)return n.textContent="Please enter a valid US phone number.",void(n.style.display="block");o.value=e.pretty}t.textContent="Message sent!",t.style.display="block",e.reset()})}function initProjectSubmission(){const e=document.getElementById("projectSubmission");if(!e)return;if(e.dataset.mounted)return;e.dataset.mounted="1",e.insertAdjacentHTML("beforeend",'\n    <form id="addProjectForm" class="stack" style="max-width:520px;margin-top:8px;">\n      <label>Title <input id="pTitle" required></label>\n\n      <label>Category\n        <select id="pCategory">\n          <option>Web Development</option>\n          <option>Responsive Design</option>\n          <option>UI/UX Design</option>\n        </select>\n      </label>\n\n      <label>Technologies (comma separated)\n        <input id="pTech" placeholder="HTML, CSS, JS">\n      </label>\n\n      <label>Description\n        <textarea id="pDesc" rows="3"></textarea>\n      </label>\n\n      <label>Image (optional)\n        <input id="pImage" placeholder="images/project.png">\n      </label>\n\n      <button type="submit">Add Project</button>\n\n      <p id="projSuccess" style="color:#32CD32;font-weight:bold;display:none;margin-top:6px;"></p>\n      <p id="projError" style="color:#ffd700;font-weight:bold;display:none;margin-top:6px;"></p>\n    </form>\n  ');const t=document.getElementById("addProjectForm"),n=document.getElementById("projSuccess"),o=document.getElementById("projError");t.addEventListener("submit",e=>{e.preventDefault(),n.style.display="none",o.style.display="none";const i=document.getElementById("pTitle").value.trim(),l=document.getElementById("pCategory").value.trim(),r=document.getElementById("pTech").value.trim(),s=document.getElementById("pDesc").value.trim(),a=document.getElementById("pImage").value.trim();if(!i)return o.textContent="Title is required.",void(o.style.display="block");const c={id:Date.now(),title:i,description:s,techStack:r?r.split(",").map(e=>e.trim()).filter(Boolean):[],category:l,image:a},d=document.getElementById("projectContainer"),p=document.getElementById("projectDetails");if(d){const e=document.createElement("div");e.className="project-card",e.innerHTML=`\n        <h3>${c.title}</h3>\n        <p>${c.description}</p>\n        <p><strong>Technologies:</strong> ${c.techStack.join(", ")}</p>\n        <p><strong>Category:</strong> ${c.category}</p>\n        ${c.image?`<img src="${c.image}" class="project-image" alt="${c.title}">`:""}\n        <button type="button" class="view-btn">View Details</button>\n      `,e.querySelector(".view-btn").addEventListener("click",()=>{p&&(p.innerHTML=`\n            <h3>${c.title}</h3>\n            <p>${c.description}</p>\n            <p><strong>Technologies:</strong> ${c.techStack.join(", ")}</p>\n            <p><strong>Category:</strong> ${c.category}</p>\n            ${c.image?`<img src="${c.image}" class="project-image" alt="${c.title}">`:""}\n          `)}),d.appendChild(e)}n.textContent="Project added!",n.style.display="block",t.reset()})}function bootForms(){initContactForm(),initProjectSubmission()}"loading"!==document.readyState?bootForms():document.addEventListener("DOMContentLoaded",bootForms,{once:!0});export{bootForms};